mapper.select:
SELECT
FROM BLOGSTABLE
ORDER BY CREATETIME DESC LIMIT #{startRow}, #{endRow}

SELECT
FROM BLOGSTABLE
ORDER BY RATE DESC LIMIT #{startRow}, #{endRow}

mapper.update:
UPDATE BLOGSTABLE 
SET BLOGS_TITLE = #{blogsTitle}, 
BLOGS_CONTENT = #{blogsContent}
WHERE BLOGS_ID = #{blogsId}

UPDATE BLOGSTABLE 
SET VIEW_COUNT = #{viewCount}
WHERE BLOGS_ID = #{blogsId}

UPDATE BLOGSTABLE 
SET COMMENTCOUNT = COMMENTCOUNT + 1
WHERE BLOGS_ID = #{blogsId}

UPDATE BLOGSTABLE 
SET COMMENTCOUNT = COMMENTCOUNT - 1
WHERE BLOGS_ID = #{blogsId}

UPDATE BLOGSTABLE 
SET RATE = #{rate}
WHERE BLOGS_ID = #{blogsId}
